export function assert(val, msg) {
  if (!val) {
    throw new Error(msg || 'assert')
  }
  return val
}

import mapValues from 'lodash/mapValues'
import chunk from 'lodash/chunk'
import zipObject from 'lodash/zipObject'

// https://developer.playfab.com/en-us/F810/limits
// Playfab has a size limit of 10k bytes per key. Swarmsim's passed that before. We can update 10 keys per push for a limit of 100k, which is enough.
export class Chunker {
  constructor(chunkSize=10000, maxChunks=10, prefix='state') {
    this.chunkSize = chunkSize
    this.maxChunks = maxChunks
    this.prefix = prefix
  }
  // encode and decode are not symmetric! They match Playfab's (asymmetric) api:
  // encode returns/set accepts {foo: 'bar'}, but decode accepts/get returns {foo: {Value: 'bar'}}
  // _normalize() removes the {Value:} layer.
  // `encode()` and `_decode()` (not `decode()`) are symmetric.
  _normalize(data) {
    return mapValues(data, (v) => v.Value)
  }
  _denormalize(data) {
    return mapValues(data, (v) => {Value: v})
  }
  encode(string) {
    let chunks = chunk(string, this.chunkSize).map(c => c.join(''))
    return zipObject(this.keys(), chunks)
  }
  decode(vdata) {
    return this._decode(this._normalize(vdata))
  }
  _decode(data) {
    return this.keys().map(key => data[key]).filter(c => !!c).join('')
  }
  keys() {
    let ret = Array(this.maxChunks).fill().map((_, i) => this.prefix + i)
    // special-case the first key: common case is only one key
    ret[0] = this.prefix
    return ret
  }
}
export default new Chunker()

import lzString from 'lz-string'

// Encode JSON state for import/export. Should not be human readable. No side effects.
// http://pieroxy.net/blog/pages/lz-string/guide.html
export class Encoder {
  encode(json) {
    return lzString.compressToUTF16(JSON.stringify(json))
  }
  decode(encoded) {
    return JSON.parse(lzString.decompressFromUTF16(encoded))
  }
}

export default new Encoder()

import encoder from './encoder'

const defaultConfig = {
  key: 'swarm-persist-state',
  localStorage: global.window && window.localStorage,
  encoder,
}

export class LocalStorageBackend {
  constructor(config) {
    this.config = Object.assign({}, defaultConfig, config)
  }
  fetch() {
    // localstorage is synchronous and doesn't really need promises, but other backends need them
    return new Promise((resolve, reject) => {
      const encoded = this.config.localStorage.getItem(this.config.key)
      if (encoded === null || encoded === undefined) {
        return resolve({empty: true})
      }
      const ret = this.config.encoder.decode(encoded)
      ret.encoded = encoded
      return resolve(ret)
    })
  }
  push(state, lastUpdated=Date.now()) {
    // localstorage is synchronous and doesn't really need promises, but other backends need them
    return new Promise((resolve, reject) => {
      const ret = {state, lastUpdated}
      const encoded = this.config.encoder.encode(ret)
      this.config.localStorage.setItem(this.config.key, encoded)
      ret.encoded = encoded
      return resolve(ret)
    })
  }
  clear(lastUpdated=Date.now()) {
    return new Promise((resolve, reject) => {
      this.config.localStorage.removeItem(this.config.key)
      resolve({lastUpdated})
    })
  }
}

import encoder from './encoder'
import {LocalStorageBackend} from './localStorageBackend'
import {Scheduler} from './scheduler'
import {assert} from './assert'

const defaultConfig = {
  encoder,
  Scheduler,
  backend: new LocalStorageBackend(),
  onFetch: function(){},
  onPull: function(){},
  onPush: function(){},
  onClear: function(){},
  // getState: required
  // setState: required

  // initState is documented as required, but it's actually not. setState with
  // a default is acceptable instead, because that's a nicer way to do things
  // when there's es6 support. It matches redux too. Example:
  // function setState(state={count: 0}) { ... }
  initState: function(){},
}

export class Persister {
  constructor(config) {
    this.config = Object.assign({}, defaultConfig, config)
    this.scheduler = new this.config.Scheduler(Object.assign({}, this.config, {persister: this}))
  }

  isStarted() { return this.scheduler.isStarted() }
  stop() {
    this.scheduler.stop()
    if (this.config.backend.stop) {
      return this.config.backend.stop()
    }
  }
  start() {
    const afterPull = fetched => {
      this.scheduler.start()
      return fetched
    }
    if (this.config.backend.start) {
      // start() is expected to fetch, to support fetch-and-login in one request.
      return this.config.backend.start().then(fetched => {
        this._pull(fetched)
        return afterPull(fetched)
      })
    }
    else {
      return this.pull().then(afterPull)
    }
  }

  fetch() {
    const promise = this.config.backend.fetch()
    this.config.onFetch(promise)
    return promise
  }
  _pull({state, empty}) {
    assert(empty || (state !== undefined), 'a persister fetch returned an undefined but nonempty state')
    if (empty) {
      // we're looking at a new player - reset their state
      state = this.config.initState()
    }
    this.config.setState(state)
    assert(this.config.getState() !== undefined, 'persister.initState() is required')
  }
  pull() {
    const promise = this.config.backend.fetch()
    promise.then(fetched => {
      this._pull(fetched)
      return fetched
    })
    this.config.onPull(promise)
    return promise
  }
  push() {
    const ret = this.config.backend.push(this.config.getState())
    this.config.onPush(ret)
    return ret
  }
  // some backends handle the last push, during page unload, differently
  _lastPush() {
    const pushFn = this.config.backend.lastPush || this.config.backend.push
    const ret = pushFn.call(this.config.backend, this.config.getState())
    this.config.onPush(ret)
    return ret
  }
  clearRemote() {
    const ret = this.config.backend.clear()
    this.config.onClear(ret)
    return ret
  }
  clear() {
    // clear both pushes (backend.clear()) and pulls (setState(initState)).
    // Note that there's no need to wait for the push to finish before
    // clearing the local state - worst case is that the clear fails; either it
    // gets overwritten in a later push anyway, or the player pushes nothing
    // and comes back later to their old save, no biggie.
    this.config.setState(this.config.initState())
    return this.clearRemote()
  }
  export() {
    return this.config.encoder.encode(this.config.getState())
  }
  import(encoded) {
    this.config.setState(this.config.encoder.decode(encoded))
  }
}

export function start(config) {
  const ret = new Persister(config)
  ret.start()
  return ret
}

import {PlayFab, PlayFabClientSDK} from 'playfab-sdk-browser'
import uuid from 'uuid'

if (global.window) {
  // expose playfab apis to user code, for login/signup
  window.PlayFabClientSDK = PlayFabClientSDK
  window.PlayFab = PlayFab
}

const defaultConfig = {
  localStorageKey: 'swarm-persist-playfab-state',
  localStorage: global.window && window.localStorage,
  stateKey: 'state',
  // try to refresh the login every 4 hours. Way more often than needed for a
  // 24 hour expiry, but it doesn't hurt.
  loginRefreshMillis: 4 * 60 * 60 * 1000,
}

class PlayFabBackendAuth {
  constructor(localStorage, key, titleId) {
    this.localStorage = localStorage
    this.key = key
    this.titleId = titleId
  }
  login(customId=this.auth.getRememberId()) {
    return new Promise((resolve, reject) => {
      PlayFabClientSDK.LoginWithCustomID({
        TitleId: this.titleId,
        CustomId: customId,
        CreateAccount: false,
        InfoRequestParameters: {
          GetUserData: true,
          GetUserAccountInfo: true,
        },
      }, (res, error) => {
        if (error) {
          return reject(error)
        }
        this.user = res
        //console.log('playfab login success')
        return resolve(res)
      })
    })
  }
  createAndRemember() {
    return new Promise((resolve, reject) => {
      this._createAndRemember(0, resolve, reject)
    })
  }
  _createAndRemember(iter=0, resolve, reject) {
    // no credentials. Create a new account.
    // We generate ids client-side. These act like userid+password; only need the
    // id to log in. But, since they're generated client-side, there's a remote
    // chance of duplicates. If we hit a duplicate, try again.
    // Hitting a dupe is actually pretty bad - someone evil could steal that
    // account. It's statistically unlikely to do that, though; ideally, we'll
    // never actually run this loop more than once.
    if (iter >= 10) {
      return reject('Failed to create new account')
    }
    let customId = uuid.v4()
    PlayFabClientSDK.LoginWithCustomID({
      TitleId: this.titleId,
      CustomId: customId,
      CreateAccount: true,
      InfoRequestParameters: {
        GetUserData: true,
        GetUserAccountInfo: true,
      },
    }, (res, error) => {
      if (error) {
        return reject(error)
      }
      if (res.data.NewlyCreated) {
        // we guessed an unused id. Yay! This is the common case.
        console.debug('created account with new id. iter '+iter)
        this.setRememberId(customId)
        this.user = res
        return resolve(res)
      }
      // we guessed a used id, try again. This should be uncommon.
      console.warn('tried creating new account with occupied id. Trying again: '+iter)
      return this._createAndRemember(iter + 1, resolve, reject)
    })
  }
  getRememberId() {
    const encoded = this.localStorage.getItem(this.key)
    return encoded ? JSON.parse(encoded).customId : null
  }
  setRememberId(customId) {
    this.localStorage.setItem(this.key, JSON.stringify({customId}))
  }
  clearRememberId() {
    this.localStorage.removeItem(this.key)
  }
  logout() {
    // Remove the custom id cookie, so we won't be logged in as this user upon page reload.
    this.clearRememberId()
    // Remove playfab's login data. There's no official method, but there's source code!
    // https://github.com/PlayFab/JavaScriptSDK/blob/master/PlayFabSDK/PlayFabClientApi.js
    PlayFab._internalSettings.sessionTicket = null
    if (PlayFabClientSDK.IsClientLoggedIn()) {
      console.error("couldn't log out from playfab")
    }
  }
  loginOrCreate() {
    const customId = this.getRememberId()
    if (customId) {
      return this.login(customId)
    }
    else {
      return this.createAndRemember()
    }
  }
}

export class PlayFabBackend {
  constructor(config) {
    this.config = Object.assign({}, defaultConfig, config)
    // it's too bad playfab forces this to be global
    PlayFab.settings.titleId = this.config.titleId
    this.auth = new PlayFabBackendAuth(this.config.localStorage, this.config.localStorageKey, this.config.titleId)
  }
  _parseFetchUserData(userdata={}) {
    // TODO chunking
    const container = userdata[this.config.stateKey]
    if (!container) {
      return {empty: true}
    }
    return {
      [this.config.stateKey]: JSON.parse(container.Value),
      lastUpdated: new Date(container.LastUpdated).getTime(),
    }
  }
  // https://api.playfab.com/Documentation/Client/method/LoginWithCustomID
  start() {
    // Login sessions expire after 24 hours:
    // https://community.playfab.com/idea/224/205582298-Session-ticket-expiry-Timestamp-.html
    //
    // Re-login to refresh the session periodically. CustomId logins are
    // invisible to the user, so there's no real cost.
    const relog = () => this.auth.login()
    this._loginRefresher = window.setInterval(relog, this.config.loginRefreshMillis)
    // login and fetch in one step
    return new Promise((resolve, reject) => {
      this.auth.loginOrCreate().then(res => {
        // login returns fetch data plus user account data
        let ret = this._parseFetchUserData(res.data.InfoResultPayload.UserData)
        ret.user = res.data.InfoResultPayload.AccountInfo
        resolve(ret)
      }, error => reject(error))
    })
  }
  logoutAndStop() {
    this.auth.logout()
    // logout without stopping doesn't make sense - you can't do anything with playfab while logged out.
    this.stop()
  }
  getRememberId() {
    this.auth.getRememberId()
  }
  setRememberId(customId) {
    this.auth.setRememberId(customId)
  }
  setRememberIdFromResponse(res) {
    const ret = res.data.InfoResultPayload.AccountInfo.CustomIdInfo.CustomId
    this.auth.setRememberId(ret)
    return ret
  }
  pullRememberId() {
    return new Promise((resolve, reject) => {
      PlayFabClientSDK.GetPlayerCombinedInfo({
        InfoRequestParameters: {GetUserAccountInfo: true},
      }, (res, error) => {
        if (error) {
          reject(error)
        }
        else {
          resolve(this.setRememberIdFromResponse(res))
        }
      })
    })
  }
  stop() {
    window.clearInterval(this._loginRefresher)
  }
  // https://api.playfab.com/Documentation/Client/method/GetUserData
  fetch() {
    return new Promise((resolve, reject) => {
      PlayFabClientSDK.GetUserData({}, (res, error) => {
        if (error) {
          reject(error)
        }
        else {
          resolve(this._parseFetchUserData(res.data.Data))
        }
      })
    })
  }
  lastPush(state) {
    // Send the last push synchronously. Pushing during page unload is tricky -
    // async XHRs fail in unload handlers, so normally, save-on-close doesn't.
    // Synchronous requests block the UI for a moment, but usually succeed.
    // UI blocking is awful, but saving the game on close is just that important.
    // 
    // PlayFab's API is pretty uncooperative here. We can't officially configure
    // its calls to be synchronous, but we can monkeypatch XMLHTTPRequest.
    // We're manipulating the XMLHttpRequest.open in this file, ExecuteRequest():
    // https://github.com/PlayFab/JavaScriptSDK/blob/master/PlayFabSDK/PlayFabClientApi.js
    //
    // sendBeacon() would be better - no UI block. It's much harder to patch in to
    // PlayFab's API though, and browser support is less common. Maybe later, but 
    // this is a fine start.
    // https://developer.mozilla.org/en-US/docs/Web/API/Navigator/sendBeacon
    const fn = window.XMLHttpRequest.prototype.open
    window.XMLHttpRequest.prototype.open = function(method, url, async) {
      console.log('hijacked xmlhttprequest.open, sync calls only')
      return fn.call(this, method, url, false)
    }
    // clean up after myself, both for success and error.
    // Maybe not necessary since the page is closing, but it's a good habit.
    const cleanup = res => {
      window.XMLHttpRequest.prototype.open = fn
      console.log('un-hijacked xmlhttprequest.open, async allowed again')
      return res
    }
    return this.push(state).then(cleanup, cleanup)
  }
  // https://api.playfab.com/Documentation/Client/method/UpdateUserData
  push(state) {
    // localstorage is synchronous and doesn't really need promises, but other backends need them
    return new Promise((resolve, reject) => {
      PlayFabClientSDK.UpdateUserData({Data: {[this.config.stateKey]: JSON.stringify(state)}}, (res, error) => {
        if (error) {
          reject(error)
        }
        else {
          resolve({state, res})
        }
      })
    })
  }
  // https://api.playfab.com/Documentation/Client/method/UpdateUserData
  clear() {
    return new Promise((resolve, reject) => {
      PlayFabClientSDK.UpdateUserData({KeysToRemove: [this.config.stateKey]}, (res, error) => {
        if (error) {
          reject(error)
        }
        else {
          resolve({res})
        }
      })
    })
  }
}

import $ from 'jquery'
import {assert} from './assert'

const defaultConfig = {
  intervalMillis: 5 * 60 * 1000,
  pushOnInterval: true,
  pushOnClose: true,
  // persister
}

export class Scheduler {
  constructor(config) {
    this.config = Object.assign({}, defaultConfig, config)
    this._push = () => {
      return this.config.persister.push()
    }
    this._lastPush = () => {
      return this.config.persister._lastPush()
    }
  }
  isStarted() {
    return !!this.runner
  }
  start() {
    assert(!this.isStarted(), 'persist scheduler already started')
    this.runner = {}

    if (this.config.pushOnInterval) {
      this.runner.interval = window.setInterval(this._push, this.config.intervalMillis)
    }
    if (this.config.pushOnClose) {
      $(window).on('unload', this._lastPush)
    }
    assert(this.isStarted())
  }
  stop() {
    assert(this.isStarted(), 'persist scheduler already stopped (or never started)')
    if (this.config.pushOnInterval) {
      window.clearInterval(this.runner.interval)
    }
    if (this.config.pushOnClose) {
      $(window).off('unload', this._lastPush)
    }
    delete this.runner
    assert(!this.isStarted())
  }
}
